mapper.select:
WITH RankedSales AS (
    SELECT apply.*, sale.sale_rate, ROW_NUMBER() OVER (PARTITION BY apply.application_seq ORDER BY sale.sale_rate) AS rn
    FROM apply
    JOIN sale ON apply.application_seq = sale.application_seq
    WHERE sale.sale_approved LIKE '%대기%' 
    and apply.sub_category_seq = #{category}
)
SELECT *
FROM RankedSales
WHERE rn = #{category}

SELECT * FROM APPLY
WHERE status like '%대기%'
and sub_category_seq = #{category}
